-- Find the greatest product of five consecutive digits in the 1000-digit number.

-- NUMBER = ""
NUMBER = "11111222221"

largest = 0
for ii = 1, (#NUMBER-5) do
  digits = string.sub(NUMBER, ii, ii+5)
  sum = string.sub(digits, 1, 1) *
    string.sub(digits, 2, 2) *
    string.sub(digits, 3, 3) *
    string.sub(digits, 4, 4) *
    string.sub(digits, 5, 5)
  largest = (sum > largest) and sum or largest
end
print(largest)
return largest
